emsToOtherPage\":{\"roles\":[\"38\"],\"title\":\"Copy or Move Repeater Items to Other Page\",\"description\":\"Add the items from a Repeater\\/RepeaterMatrix field on one page to the same field on another page and optionally remove them from the original page.\",\"notes\":\"If the field on the destination page already has items, you can choose to append, or overwrite.\",\"icon\":\"copy\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"CopyTableFieldRowsToOtherPage\":{\"roles\":[\"38\"],\"title\":\"Copy Table Field Rows to Other Page\",\"description\":\"Add the rows from a Table field on one page to the same field on another page.\",\"notes\":\"If the field on the destination page already has rows, you can choose to append, or overwrite.\",\"icon\":\"copy\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"CreateUsersBatcher\":{\"roles\":[\"38\"],\"title\":\"Create Users Batcher\",\"description\":\"Allows you to batch create users.\",\"notes\":\"Having the Email New User module installed and configured to generate a password automatically is recommended. It is also recommended that you install the Password Force Change module.\",\"icon\":\"user-plus\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"DeleteUnusedFields\":{\"roles\":[\"38\"],\"title\":\"Delete Unused Fields\",\"description\":\"Deletes fields that are not used by any templates.\",\"notes\":\"Shows a list of unused fields with checkboxes to select those to delete.\",\"icon\":\"minus-circle\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"DeleteUnusedTemplates\":{\"roles\":[\"38\"],\"title\":\"Delete Unused Templates\",\"description\":\"Deletes templates that are not used by any pages.\",\"notes\":\"Shows a list of unused templates with checkboxes to select those to delete.\",\"icon\":\"minus-circle\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"EmailBatcher\":{\"roles\":[\"38\"],\"title\":\"Email Batcher\",\"description\":\"Lets you email multiple addresses at once.\",\"notes\":\"You can select \\\"Pages\\\" or \\\"User Roles\\\" for determining the recipients.\",\"icon\":\"envelope\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"FieldSetOrSearchAndReplace\":{\"roles\":[\"38\"],\"title\":\"Field Set or Search and Replace\",\"description\":\"Set field values, or search and replace text in field values from a filtered selection of pages and fields.\",\"notes\":\"This can be very destructive - please be careful!\",\"icon\":\"search-plus\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"FtpFilesToPage\":{\"roles\":[\"38\"],\"title\":\"FTP Files to Page\",\"description\":\"Add files\\/images from a folder to a selected page.\",\"notes\":\"This is useful if you want to FTP files, rather than upload via the admin.\",\"icon\":\"upload\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"PageActiveLanguagesBatcher\":{\"roles\":[\"38\"],\"title\":\"Page Active Languages Batcher\",\"description\":\"Lets you enable or disable active status of multiple languages on multiple pages at once.\",\"notes\":\"\",\"icon\":\"language\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"PageManipulator\":{\"roles\":[\"38\"],\"title\":\"Page Manipulator\",\"description\":\"Uses an InputfieldSelector to query pages and then allows batch actions on the matched pages.\",\"notes\":\"Actions are: Publish, Unpublish, Hide, Unhide, Trash, Delete, Change Template, Change Parent\",\"icon\":\"cog\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"PageTableToRepeaterMatrix\":{\"roles\":[\"38\"],\"title\":\"Page Table to Repeater \\/ Repeater Matrix\",\"description\":\"Fully converts an existing (and populated) PageTable field to either a Repeater or RepeaterMatrix field.\",\"notes\":\"By default it will choose Repeater or RepeaterMatrix based on the number of templates in the PageTable field, but you can also choose to force RepeaterMatrix.\",\"icon\":\"exchange\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"TemplateFieldsBatcher\":{\"roles\":[\"38\"],\"title\":\"Template Fields Batcher\",\"description\":\"Lets you add or remove multiple fields from multiple templates at once.\",\"notes\":\"\",\"icon\":\"cube\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"},\"menu\":\"1\"},\"TemplateRolesBatcher\":{\"roles\":[\"38\"],\"title\":\"Template Roles Batcher\",\"description\":\"Lets you add or remove access permissions, for multiple roles and multiple templates at once.\",\"notes\":\"Access permission options are: Edit Pages, Create Pages, and Add Children.\",\"icon\":\"cubes\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}},\"UserRolesPermissionsBatcher\":{\"roles\":[\"38\"],\"title\":\"User Roles Permission Batcher\",\"description\":\"Lets you add or remove permissions for multiple roles, or roles for multiple users at once.\",\"notes\":\"Role selections are required. If all three have selections, permissions will be modified in roles and roles modified in users.\",\"icon\":\"cogs\",\"author\":\"Adrian Jones\",\"authorLinks\":{\"pwforum\":\"985-adrian\",\"pwdirectory\":\"adrian-jones\",\"github\":\"adrianbj\"}}},\"dbBackup\":\"automatic\",\"showActionCode\":null,\"-dups\":[\"\\/site\\/modules\\/ProcessAdminActions\\/ProcessAdminActions.module.php\",\"\\/site\\/modules\\/ProcessAdminActions-master\\/ProcessAdminActions.module.php\"],\"-dups-use\":\"\\/site\\/modules\\/ProcessAdminActions\\/ProcessAdminActions.module.php\"}', '2024-06-28 00:30:24'),
